// src/index.js
const ContractGenerator = require('./contractGenerator');
const EmailSender = require('./emailSender');
const MessageTemplates = require('./messageTemplates');

module.exports = {
    // Exportar clases principales
    ContractGenerator,
    EmailSender,
    MessageTemplates,
    
    // Métodos directos para uso rápido
    generateRentalContract: (clientData, propertyData) => {
        const generator = new ContractGenerator();
        return generator.generateRentalContract(clientData, propertyData);
    },
    
    sendAcceptanceEmail: (recipientEmail, clientName, contractDetails, bankDetails, attachmentPath) => {
        const emailSender = new EmailSender();
        return emailSender.sendAcceptanceEmail(recipientEmail, clientName, contractDetails, bankDetails, attachmentPath);
    },
    
    sendRejectionEmail: (recipientEmail, clientName, reason) => {
        const emailSender = new EmailSender();
        return emailSender.sendRejectionEmail(recipientEmail, clientName, reason);
    },
    
    // Métodos de templates de mensajes
    getAppointmentApprovalTemplate: (clientName, appointmentDetails) => {
        return MessageTemplates.getAppointmentApproval(clientName, appointmentDetails);
    },
    
    getAppointmentRejectionTemplate: (clientName, reason) => {
        return MessageTemplates.getAppointmentRejection(clientName, reason);
    }
};